import { Component } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {
  zones = [
    { name: 'International Zone', img: 'assets/zones/International zone.png' },
    { name: 'Entrepreneur Zone', img: 'assets/zones/Entrepreneur zone.png' },
    { name: 'Placement Zone', img: 'assets/zones/Placement zone.png' }
  ];
}
